// Your first JavaScript program
console.log('Hello, World!');

// You can also show an alert
alert('Welcome to JavaScript!');

// Using let (can be changed)
let name = 'Alice';
let age = 12;

// Using const (cannot be changed)
const PI = 3.14159;
const schoolName = 'Modern Age Coders';

// Changing a variable
name = 'Bob';
age = age + 1;

console.log(name); // Output: Bob
console.log(age);  // Output: 13

// Different data types
let message = 'Hello!';           // String
let score = 95;                   // Number
let isPassing = true;             // Boolean
let colors = ['red', 'blue'];     // Array
let student = {                   // Object
    name: 'Alice',
    grade: 7
};

// Define a function
function greet(name) {
    return 'Hello, ' + name + '!';
}

// Use the function
console.log(greet('Alice'));  // Output: Hello, Alice!
console.log(greet('Bob'));    // Output: Hello, Bob!

// Function with multiple parameters
function add(a, b) {
    return a + b;
}

console.log(add(5, 3));  // Output: 8

let age = 15;

if (age >= 18) {
    console.log('You are an adult');
} else if (age >= 13) {
    console.log('You are a teenager');
} else {
    console.log('You are a child');
}

// Output: You are a teenager

// For loop - count from 1 to 5
for (let i = 1; i <= 5; i++) {
    console.log('Count: ' + i);
}

// While loop - repeat while condition is true
let countdown = 3;
while (countdown > 0) {
    console.log(countdown);
    countdown--;
}
console.log('Blast off!');

// Create an array
let fruits = ['apple', 'banana', 'orange'];

// Access array elements (starts at 0)
console.log(fruits[0]);  // Output: apple

// Add to array
fruits.push('grape');

// Loop through array
for (let i = 0; i < fruits.length; i++) {
    console.log(fruits[i]);
}

function calculator(num1, num2, operation) {
    if (operation === 'add') {
        return num1 + num2;
    } else if (operation === 'subtract') {
        return num1 - num2;
    } else if (operation === 'multiply') {
        return num1 * num2;
    } else if (operation === 'divide') {
        return num1 / num2;
    } else {
        return 'Invalid operation';
    }
}

// Test the calculator
console.log(calculator(10, 5, 'add'));       // Output: 15
console.log(calculator(10, 5, 'subtract'));  // Output: 5
console.log(calculator(10, 5, 'multiply'));  // Output: 50
console.log(calculator(10, 5, 'divide'));    // Output: 2
