# Validate email format
def is_valid_email(email):
    return '@' in email and '.' in email

print(is_valid_email('user@example.com'))  # True
print(is_valid_email('invalid.email'))     # False

# Calculate grade from score
def get_grade(score):
    if score >= 90:
        return 'A'
    elif score >= 80:
        return 'B'
    elif score >= 70:
        return 'C'
    elif score >= 60:
        return 'D'
    else:
        return 'F'

student_score = 87
print(f'Score {student_score} = Grade {get_grade(student_score)}')

# Check if number is prime
def is_prime(number):
    if number < 2:
        return False
    for i in range(2, number):
        if number % i == 0:
            return False
    return True

for num in range(1, 11):
    if is_prime(num):
        print(f'{num} is prime')

# Create a dictionary
student = {
    'name': 'Alice',
    'age': 15,
    'grade': 'A',
    'subjects': ['Math', 'Science', 'English']
}

# Access values
print(student['name'])    # Alice
print(student['age'])     # 15

# Add or modify values
student['school'] = 'Modern Age Coders'
student['age'] = 16  # Update age

# Check if key exists
if 'grade' in student:
    print(f"Grade: {student['grade']}")

# Loop through dictionary
for key, value in student.items():
    print(f'{key}: {value}')

# Practical example: Word counter
text = 'python is awesome python is fun'
words = text.split()
word_count = {}

for word in words:
    if word in word_count:
        word_count[word] += 1
    else:
        word_count[word] = 1

print(word_count)  # {'python': 2, 'is': 2, 'awesome': 1, 'fun': 1}

# Basic error handling
try:
    number = int(input('Enter a number: '))
    result = 100 / number
    print(f'Result: {result}')
except ValueError:
    print('That\'s not a valid number!')
except ZeroDivisionError:
    print('Cannot divide by zero!')

# Practical example: Safe file reading
try:
    age = int(input('Enter your age: '))
    if age < 0:
        raise ValueError('Age cannot be negative')
    print(f'You are {age} years old')
except ValueError as e:
    print(f'Error: {e}')

# Always execute code with finally
try:
    file = open('data.txt', 'r')
    content = file.read()
    print(content)
except FileNotFoundError:
    print('File not found!')
finally:
    print('This always runs')
